function buildPortfolioCard(o,r,i,t,a,n){let d=`\n  <li class="portfolio__card-grid__card-container">\n    <a class="portfolio__card-grid__card-container__card" id="PortfolioItemLink" href="${o}">\n      <div class="portfolio__card-grid__card-container__card__img" id="PortfolioItemImage" style="background-image: ${r="url('https://raw.githubusercontent.com/ricardogouveia3/rcrd-portfolio/master/"+r+"')"}">\n      </div>\n      <div class="portfolio__card-grid__card-container__card__info">\n        <span class="portfolio__card-grid__card-container__card__info__job-type" id="PortfolioItemType" style="border-color: ${n};">${i}</span>\n        <h4 class="portfolio__card-grid__card-container__card__info__job-title" id="PortfolioItemTitle">${t}</h4>\n        <span class="portfolio__card-grid__card-container__card__info__job-date" id="PortfolioItemDate">${a}</span>\n      </div>\n    </a>\n  </li>\n  `;document.getElementById("portfolioCardGrid").innerHTML+=d}document.body.classList.remove("no-js"),document.body.classList.add("js"),fetch("https://raw.githubusercontent.com/ricardogouveia3/rcrd-portfolio/master/data/portfolio.json").then(function(o){o.json().then(function(o){for(var r of o.projects)buildPortfolioCard(r.link,r.image,r.type,r.title,r.date,r.color)})}).catch(function(o){console.error("Failed retrieving portfolio information",o)});